from bs4 import BeautifulSoup
import re


def extract_text_recursive(element):
    text = ""
    for content in element.contents:
        if isinstance(content, str):
            text += content
        elif content.name == 'span':
            # Extract the word after the translation= attribute using regex
            translation_match = re.search(r'translation="([^"]+)"', str(content))
            if translation_match:
                text += f"{translation_match.group(1)}"
        else:
            text += extract_text_recursive(content)
    return text.strip()

def extract_text_and_index_from_html(html_content):
    soup = BeautifulSoup(html_content, 'html.parser')

    # Extract all text from the HTML document
    all_text = extract_text_recursive(soup)

    # Find and index highlighted words using regular expressions
    highlighted_words = re.findall(r'\b(\w+)\b', all_text)

    # Create a dictionary to store the indexed words and their positions
    index_dict = {}
    for position, word in enumerate(highlighted_words):
        if word.lower() not in index_dict:
            index_dict[word.lower()] = []
        index_dict[word.lower()].append(position)

    return all_text, index_dict


def create_jsonl_structure(html_content):
    all_text, index_dict = extract_text_and_index_from_html(html_content)

    # Create a list to store JSONL entries
    jsonl_entries = []

    # Iterate through the sentences and extract the highlighted word
    for sentence in re.split(r'(?<!\w\.\w.)(?<![A-Z][a-z]\.)(?<=\.|\?)\s', all_text):
        highlighted_word = None

        # Find the highlighted word in the sentence
        for word in re.findall(r'\b(\w+)\b', sentence):
            if word.lower() in index_dict:
                highlighted_word = word
                break

        # Create a JSONL entry
        json_entry = {
            "input_sentence": sentence,
            "highlighted_word": highlighted_word
        }

        # Add the entry to the list
        jsonl_entries.append(json_entry)

    return jsonl_entries

